forever(function () {
    if (mySprite.y < 100) {
        scene.centerCameraAt(mySprite.x, mySprite.y * 1 + 50)
    } else {
        scene.centerCameraAt(mySprite.x, mySprite.y * 0.3 + 120)
    }
    if (mySprite.vy == 0) {
        mySprite.ax = 0
        if (Mario_current_vy == 0) {
            controller.moveSprite(mySprite, 100, 0)
            Mario_current_vx = mySprite.vx
        } else {
            mySprite.setVelocity(0, 0)
            Mario_current_vy = 0
        }
    } else {
        Mario_current_vy = mySprite.vy
        mySprite.vx = Mario_current_vx
    }
})
